mapper.update:
update rdos_engine_job_cache
set node_address = #{nodeAddress}, stage = #{stage}, job_priority = #{jobPriority}, group_name = #{groupName}, gmt_modified=now()
where job_id = #{jobId}

mapper.select:
select * from rdos_engine_job_cache
where id > #{id} and (node_address = #{nodeAddress} or node_address is null)

    and stage = #{stage}


    and engine_type = #{engineType}

and is_deleted=0 order by job_priority asc limit 500;

select * from rdos_engine_job_cache WHERE job_id in
#{jobId}
and is_deleted=0

select job_name from rdos_engine_job_cache
where job_name like '%${jobName}%' and compute_type = #{computeType}
and stage = 1 and is_deleted=0;

select count(1) from rdos_engine_job_cache
where engine_type = #{engineType} and group_name = #{groupName} and node_address = #{nodeAddress}

    and stage = #{stage}

and is_deleted=0;
