mapper.select:
select job_id, engine_job_id,application_id,plugin_info_id,status,engine_log,log_info,exec_start_time,exec_end_time,exec_time,retry_num,retry_task_params,gmt_create,gmt_modified,is_deleted,source_type,compute_type from schedule_engine_job
where job_id IN

    #{jobId}

and is_deleted=0;

select job_id from schedule_engine_job
where status = #{status}

    and compute_type = #{computeType}

and is_deleted=0

select job_id as jobId,status,exec_start_time as execStartTime,exec_end_time as execEndTime,exec_time as execTime,retry_num as retryNum
from schedule_engine_job
where gmt_modified >= #{time} and compute_type = #{computeType}
and is_deleted=0;

mapper.update:
update rdos_engine_job
set gmt_modified=now(), status=#{status}
where job_id IN

    #{jobId}

and is_deleted=0;
