mapper.update:
update dt_alert_gate
alert_gate_name=#{alertGateName}, alert_gate_type=#{alertGateType}, alert_gate_code=#{alertGateCode}, alert_gate_status=#{alertGateStatus}, alert_gate_json=#{alertGateJson}, alert_gate_source=#{alertGateSource}, file_path=#{filePath}, is_deleted=#{isDeleted},
where id = #{id}

update dt_alert_gate
set is_deleted = 1
where id = #{gateId}

mapper.select:
select 
FROM dt_alert_gate
WHERE is_deleted = 0
AND alert_gate_type=#{alertGateType}
AND alert_gate_code=#{alertGateCode}
AND alert_gate_status=#{alertGateStatus}
AND alert_gate_source=#{alertGateSource}
order by id desc

select 
FROM dt_alert_gate
WHERE is_deleted = 0

AND id = #{id}
AND alert_gate_type=#{alertGateType}
AND alert_gate_code=#{alertGateCode}
AND alert_gate_status=#{alertGateStatus}
AND alert_gate_source=#{alertGateSource}

limit 1
