mapper.select:
select job_id, engine_job_id,plugin_info_id,status,engine_log,log_info,exec_start_time,exec_end_time,exec_time,gmt_create,gmt_modified,is_deleted from rdos_engine_batch_job
where job_id IN

    #{jobId}

and is_deleted=0;

mapper.update:
UPDATE rdos_engine_batch_job
SET status = #{status} , log_info = #{logInfo}, engine_log = #{engineLog}, gmt_modified = #{gmtModified}
WHERE job_id = #{jobId} and is_deleted=0

UPDATE  rdos_engine_batch_job
set status = #{status} where status = 0 and
job_id in (select job_id from rdos_batch_job where job_name like #{fillDataJobNameLike} and project_id = #{projectId});
