mapper.update:
update rdos_engine_job
set gmt_modified=now(), exec_end_time=now(),engine_job_id=#{engineId},status=#{status},application_id=#{appId}
where job_id=#{jobId};

mapper.select:
select job_id, engine_job_id,plugin_info_id,status,engine_log,log_info,exec_start_time,exec_end_time,exec_time,retry_num,retry_task_params,gmt_create,gmt_modified,is_deleted,source_type,compute_type from rdos_engine_job
where job_id IN

    #{jobId}

and is_deleted=0;
